mapper.select:
SELECT * FROM todo_list
WHERE user_id = #{userId}
AND date = #{date}
AND content = #{content}

SELECT id, date, content, is_mission, is_checked, link, user_id AS userId 
FROM todo_list 
WHERE id = #{id}

mapper.update:
UPDATE todo_list
SET is_checked = NOT is_checked
WHERE id = #{id};
